import React, { useState } from 'react';
import './App.css';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [employees, setEmployees] = useState([
    { 
      id: 1, 
      name: 'Digvijay Kumar Singh', 
      department: 'MCA', 
      designation: 'Professor', 
      salary: '70000', 
      email: 'chauhandigvijay669@gmail.com' 
    },
    { 
      id: 2, 
      name: 'Rahul Sharma', 
      department: 'IT', 
      designation: 'Senior Developer', 
      salary: '85000', 
      email: 'rahul@company.com' 
    }
  ]);

  const [formData, setFormData] = useState({
    name: '',
    department: '',
    designation: '',
    salary: '',
    email: ''
  });

  const [editingId, setEditingId] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.name && formData.email && formData.designation && formData.salary && formData.department) {
      if (editingId) {
        // Update existing employee
        setEmployees(employees.map(emp => 
          emp.id === editingId ? { ...formData, id: editingId } : emp
        ));
        setEditingId(null);
      } else {
        // Add new employee
        const newEmployee = {
          id: Date.now(),
          ...formData
        };
        setEmployees([...employees, newEmployee]);
      }
      setFormData({ name: '', department: '', designation: '', salary: '', email: '' });
    }
  };

  const handleEdit = (employee) => {
    setFormData({
      name: employee.name,
      department: employee.department,
      designation: employee.designation,
      salary: employee.salary,
      email: employee.email
    });
    setEditingId(employee.id);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this employee?')) {
      setEmployees(employees.filter(emp => emp.id !== id));
    }
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setFormData({ name: '', department: '', designation: '', salary: '', email: '' });
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`min-h-screen relative overflow-hidden font-sans transition-colors duration-500 ${
      isDarkMode ? 'bg-[#0f0f23]' : 'bg-gray-100'
    }`}>
      {/* Animated Background - Dark Mode */}
      {isDarkMode && (
        <div className="fixed inset-0 -z-10">
          <div className="absolute inset-0 bg-gradient-to-br from-purple-900/30 via-transparent to-cyan-900/30 animate-pulse"></div>
          <div className="absolute top-0 left-0 w-96 h-96 bg-purple-500/20 rounded-full blur-3xl animate-blob"></div>
          <div className="absolute top-0 right-0 w-96 h-96 bg-cyan-500/20 rounded-full blur-3xl animate-blob animation-delay-2000"></div>
          <div className="absolute bottom-0 left-1/2 w-96 h-96 bg-pink-500/20 rounded-full blur-3xl animate-blob animation-delay-4000"></div>
        </div>
      )}

      {/* Light Mode Background */}
      {!isDarkMode && (
        <div className="fixed inset-0 -z-10 bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50"></div>
      )}

      {/* Theme Toggle Button */}
      <button
        onClick={toggleTheme}
        className={`fixed top-6 right-6 z-50 p-4 rounded-full shadow-lg transition-all duration-300 ${
          isDarkMode 
            ? 'bg-white/10 backdrop-blur-md border border-white/20 text-yellow-400 hover:bg-white/20' 
            : 'bg-gray-800 text-yellow-400 hover:bg-gray-700'
        }`}
      >
        {isDarkMode ? (
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
          </svg>
        ) : (
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
          </svg>
        )}
      </button>

      {/* Main Container */}
      <div className="max-w-7xl mx-auto px-4 py-12 md:py-16">
        <div className={`rounded-3xl border shadow-2xl p-6 md:p-10 transition-all duration-500 ${
          isDarkMode 
            ? 'bg-white/10 backdrop-blur-xl border-white/20 hover:shadow-cyan-500/20' 
            : 'bg-white border-gray-200 shadow-xl'
        }`}>

          {/* Header */}
          <div className="text-center mb-10">
            <h1 className={`text-4xl md:text-6xl font-bold mb-3 ${
              isDarkMode 
                ? 'bg-gradient-to-r from-cyan-400 via-purple-500 to-pink-500 bg-clip-text text-transparent animate-gradient'
                : 'text-gray-800'
            }`}>
              EMPLOYEE MANAGEMENT
            </h1>
            <p className={`text-sm md:text-base ${isDarkMode ? 'text-white/60' : 'text-gray-600'}`}>
              Manage your team with style ✨
            </p>
          </div>

          {/* Add/Edit Employee Form */}
          <form onSubmit={handleSubmit} className="mb-12">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <input
                type="text"
                placeholder="Employee Name"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                className={`px-5 py-4 rounded-2xl border focus:outline-none focus:ring-2 transition-all duration-300 ${
                  isDarkMode
                    ? 'bg-white/5 backdrop-blur-md border-white/20 text-white placeholder-white/50 focus:border-cyan-400 focus:bg-cyan-400/10 focus:ring-cyan-400/50'
                    : 'bg-white border-gray-300 text-gray-800 placeholder-gray-400 focus:border-purple-500 focus:ring-purple-500/50'
                }`}
                required
              />

              <input
                type="text"
                placeholder="Department"
                value={formData.department}
                onChange={(e) => setFormData({...formData, department: e.target.value})}
                className={`px-5 py-4 rounded-2xl border focus:outline-none focus:ring-2 transition-all duration-300 ${
                  isDarkMode
                    ? 'bg-white/5 backdrop-blur-md border-white/20 text-white placeholder-white/50 focus:border-cyan-400 focus:bg-cyan-400/10 focus:ring-cyan-400/50'
                    : 'bg-white border-gray-300 text-gray-800 placeholder-gray-400 focus:border-purple-500 focus:ring-purple-500/50'
                }`}
                required
              />

              <input
                type="text"
                placeholder="Designation"
                value={formData.designation}
                onChange={(e) => setFormData({...formData, designation: e.target.value})}
                className={`px-5 py-4 rounded-2xl border focus:outline-none focus:ring-2 transition-all duration-300 ${
                  isDarkMode
                    ? 'bg-white/5 backdrop-blur-md border-white/20 text-white placeholder-white/50 focus:border-cyan-400 focus:bg-cyan-400/10 focus:ring-cyan-400/50'
                    : 'bg-white border-gray-300 text-gray-800 placeholder-gray-400 focus:border-purple-500 focus:ring-purple-500/50'
                }`}
                required
              />

              <input
                type="number"
                placeholder="Salary"
                value={formData.salary}
                onChange={(e) => setFormData({...formData, salary: e.target.value})}
                className={`px-5 py-4 rounded-2xl border focus:outline-none focus:ring-2 transition-all duration-300 ${
                  isDarkMode
                    ? 'bg-white/5 backdrop-blur-md border-white/20 text-white placeholder-white/50 focus:border-cyan-400 focus:bg-cyan-400/10 focus:ring-cyan-400/50'
                    : 'bg-white border-gray-300 text-gray-800 placeholder-gray-400 focus:border-purple-500 focus:ring-purple-500/50'
                }`}
                required
              />

              <input
                type="email"
                placeholder="Email Address"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                className={`px-5 py-4 rounded-2xl border focus:outline-none focus:ring-2 transition-all duration-300 md:col-span-2 ${
                  isDarkMode
                    ? 'bg-white/5 backdrop-blur-md border-white/20 text-white placeholder-white/50 focus:border-cyan-400 focus:bg-cyan-400/10 focus:ring-cyan-400/50'
                    : 'bg-white border-gray-300 text-gray-800 placeholder-gray-400 focus:border-purple-500 focus:ring-purple-500/50'
                }`}
                required
              />
            </div>

            <div className="flex gap-3">
              <button
                type="submit"
                className={`flex-1 px-6 py-4 rounded-2xl font-semibold uppercase tracking-wider hover:-translate-y-1 transition-all duration-300 relative overflow-hidden group ${
                  isDarkMode
                    ? 'bg-gradient-to-r from-cyan-500 to-purple-600 text-white hover:shadow-lg hover:shadow-cyan-400/50'
                    : 'bg-gradient-to-r from-purple-600 to-pink-600 text-white hover:shadow-lg hover:shadow-purple-500/50'
                }`}
              >
                <span className="relative z-10">{editingId ? '✏️ Update Employee' : '➕ Add Employee'}</span>
              </button>

              {editingId && (
                <button
                  type="button"
                  onClick={handleCancelEdit}
                  className={`px-6 py-4 rounded-2xl font-semibold uppercase tracking-wider hover:-translate-y-1 transition-all duration-300 ${
                    isDarkMode
                      ? 'bg-red-500/20 text-red-400 border border-red-500/30 hover:bg-red-500/30'
                      : 'bg-red-100 text-red-600 border border-red-300 hover:bg-red-200'
                  }`}
                >
                  Cancel
                </button>
              )}
            </div>
          </form>

          {/* Employee Count */}
          <div className="mb-6 flex justify-between items-center">
            <h2 className={`text-2xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
              All Employees
            </h2>
            <span className={`px-4 py-2 backdrop-blur-md rounded-full font-semibold border ${
              isDarkMode
                ? 'bg-cyan-500/20 text-cyan-400 border-cyan-500/30'
                : 'bg-purple-100 text-purple-700 border-purple-300'
            }`}>
              {employees.length} Employees
            </span>
          </div>

          {/* Employee Table */}
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead>
                <tr className={`backdrop-blur-md ${
                  isDarkMode
                    ? 'bg-gradient-to-r from-cyan-500/20 to-purple-500/20'
                    : 'bg-gradient-to-r from-purple-100 to-pink-100'
                }`}>
                  <th className={`py-5 px-4 md:px-6 text-left font-semibold uppercase text-xs md:text-sm tracking-wider rounded-l-2xl ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Name</th>
                  <th className={`py-5 px-4 md:px-6 text-left font-semibold uppercase text-xs md:text-sm tracking-wider hidden md:table-cell ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Department</th>
                  <th className={`py-5 px-4 md:px-6 text-left font-semibold uppercase text-xs md:text-sm tracking-wider hidden lg:table-cell ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Designation</th>
                  <th className={`py-5 px-4 md:px-6 text-left font-semibold uppercase text-xs md:text-sm tracking-wider hidden lg:table-cell ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Salary</th>
                  <th className={`py-5 px-4 md:px-6 text-left font-semibold uppercase text-xs md:text-sm tracking-wider ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Email</th>
                  <th className={`py-5 px-4 md:px-6 text-center font-semibold uppercase text-xs md:text-sm tracking-wider rounded-r-2xl ${
                    isDarkMode ? 'text-white' : 'text-gray-700'
                  }`}>Actions</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((employee, index) => (
                  <tr 
                    key={employee.id}
                    className={`border-b transition-all duration-300 group ${
                      isDarkMode
                        ? 'bg-white/5 backdrop-blur-md border-white/10 hover:bg-white/10 hover:translate-x-2'
                        : 'bg-white border-gray-200 hover:bg-purple-50 hover:translate-x-2'
                    }`}
                    style={{ animationDelay: `${index * 100}ms` }}
                  >
                    <td className={`py-4 px-4 md:px-6 font-semibold text-sm md:text-base ${
                      isDarkMode ? 'text-cyan-400' : 'text-purple-600'
                    }`}>
                      <div className="flex items-center gap-3">
                        <div className={`w-10 h-10 rounded-full flex items-center justify-center text-white font-bold ${
                          isDarkMode
                            ? 'bg-gradient-to-br from-cyan-400 to-purple-500'
                            : 'bg-gradient-to-br from-purple-500 to-pink-500'
                        }`}>
                          {employee.name.charAt(0)}
                        </div>
                        <span className="hidden sm:inline">{employee.name}</span>
                        <span className="sm:hidden">{employee.name.split(' ')[0]}</span>
                      </div>
                    </td>
                    <td className={`py-4 px-4 md:px-6 text-xs md:text-base hidden md:table-cell ${
                      isDarkMode ? 'text-white/90' : 'text-gray-700'
                    }`}>{employee.department}</td>
                    <td className={`py-4 px-4 md:px-6 text-sm hidden lg:table-cell ${
                      isDarkMode ? 'text-white/70' : 'text-gray-600'
                    }`}>{employee.designation}</td>
                    <td className={`py-4 px-4 md:px-6 text-sm hidden lg:table-cell ${
                      isDarkMode ? 'text-white/70' : 'text-gray-600'
                    }`}>₹{employee.salary}</td>
                    <td className={`py-4 px-4 md:px-6 text-xs md:text-base ${
                      isDarkMode ? 'text-white/90' : 'text-gray-700'
                    }`}>{employee.email}</td>
                    <td className="py-4 px-4 md:px-6">
                      <div className="flex gap-2 justify-center">
                        <button
                          onClick={() => handleEdit(employee)}
                          className={`px-3 py-2 rounded-xl border font-semibold text-xs md:text-sm transition-all duration-300 hover:scale-105 ${
                            isDarkMode
                              ? 'bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 border-blue-500/30 hover:border-blue-500/50'
                              : 'bg-blue-100 hover:bg-blue-200 text-blue-600 border-blue-300'
                          }`}
                        >
                          ✏️ Edit
                        </button>
                        <button
                          onClick={() => handleDelete(employee.id)}
                          className={`px-3 py-2 rounded-xl border font-semibold text-xs md:text-sm transition-all duration-300 hover:scale-105 ${
                            isDarkMode
                              ? 'bg-red-500/20 hover:bg-red-500/30 text-red-400 border-red-500/30 hover:border-red-500/50'
                              : 'bg-red-100 hover:bg-red-200 text-red-600 border-red-300'
                          }`}
                        >
                          🗑️ Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Empty State */}
          {employees.length === 0 && (
            <div className="text-center py-12">
              <div className="text-6xl mb-4">👥</div>
              <p className={`text-lg ${isDarkMode ? 'text-white/60' : 'text-gray-600'}`}>
                No employees yet. Add your first team member!
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
